import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import "./App.css";
import Navbar from "./components/Navbar";
import Home from "./components/Home";
import Products from "./components/Products";
import ProductAdmin from "./components/ProductAdmin";
import LogIn from "./components/auth/LogIn";
import Register from "./components/auth/Register";
import ForgotPassword from "./components/auth/ForgotPassword";
import ForgotPasswordVerification from "./components/auth/ForgotPasswordVerification";
import ChangePassword from "./components/auth/ChangePassword";
import ChangePasswordConfirm from "./components/auth/ChangePasswordConfirm";
import Welcome from "./components/auth/Welcome";
import Footer from "./components/Footer";
import { library } from "@fortawesome/fontawesome-svg-core";
import { faEdit } from "@fortawesome/free-solid-svg-icons";
library.add(faEdit);

class App extends Component {
  state = {
    isAuthenticated: false,
    user: null
  };

  // Helper functions to change state when user is logged in
  setAuthStatus = authenticated => {
    this.setState({ isAuthenticated: authenticated });
  };

  setUser = user => {
    this.setState({ user: user });
  };

  render() {
    const authProps = {
      isAuthenticated: this.state.isAuthenticated,
      user: this.state.user,
      setAuthStatus: this.setAuthStatus,
      setUser: this.setUser
    };
    // Use special render syntax to pass properties to each component
    return (
      <div className="App">
        <Router>
          <div>
            <Navbar auth={authProps} />
            <Switch>
              {/* Pass in default props along with user created props */}
              <Route
                exact
                path="/"
                render={props => <Home {...props} auth={authProps} />}
              />
              <Route
                exact
                path="/products"
                render={props => <Products {...props} auth={authProps} />}
              />
              <Route
                exact
                path="/admin"
                render={props => <ProductAdmin {...props} auth={authProps} />}
              />
              <Route
                exact
                path="/login"
                render={props => <LogIn {...props} auth={authProps} />}
              />
              <Route
                exact
                path="/register"
                render={props => <Register {...props} auth={authProps} />}
              />
              <Route
                exact
                path="/forgotpassword"
                render={props => <ForgotPassword {...props} auth={authProps} />}
              />
              <Route
                exact
                path="/forgotpasswordverification"
                render={props => (
                  <ForgotPasswordVerification {...props} auth={authProps} />
                )}
              />
              <Route
                exact
                path="/changepassword"
                render={props => <ChangePassword {...props} auth={authProps} />}
              />
              <Route
                exact
                path="/changepasswordconfirmation"
                component={ChangePasswordConfirm}
              />
              <Route
                exact
                path="/welcome"
                render={props => <Welcome {...props} auth={authProps} />}
              />
            </Switch>
            <Footer />
          </div>
        </Router>
      </div>
    );
  }
}

export default App;
